import React, { useEffect, useRef, useState } from 'react';
import moment from 'moment';
import '../src/App.scss';
import lottie from 'lottie-web';
import { useForm, useWatch } from 'react-hook-form';
import PlaceholderCards from './Components/PlaceholderCards';
import CardDetail from './Components/CardDetail';

function App() {

  const apiKey = "8bd04b4c4bc8f493a35b227dba34ae78";

  const [latitude, setLatitude] = useState(0);
  const [longitude, setlongitude] = useState(0);
  const [userLocation, setUserLocation] = useState([]);
  const [weather, setWeather] = useState([]);
  const [days, setDays] = useState([]);
  const [loading, setLoading] = useState(false);
  const [customLocation, setCustomLocation] = useState({
    customLatitude: null,
    customLongitude: null
  })
  const [cardDetail, setCardDetail] = useState([]);
  const [cardDetailVisible, setCardDetailVisible] = useState(false);
  const [usingCurrentLocation, setUsingCurrentLocation] =useState(null);

  //Handle submit
  const {register,watch,handleSubmit,formState: { errors },} = useForm();
  const onSubmit = (data) => getCustomLocation(data);

  const container = useRef(null);
  const animContainer = useRef([]);

  const loaderAnim = () => {
    lottie.loadAnimation({
      container: container.current,
      renderer: 'svg',
      loop: true,
      autoplay: true,
      animationData: require('../src/svg/loader.json')
    })
  }

  const sunnyAnim = () => {
    lottie.loadAnimation({
      container: animContainer.current,
      renderer: 'svg',
      loop: true,
      autoplay: true,
      animationData: require('../src/svg/sunny.json')
    })
    console.log('loading sunny anim');
  }

  //Check for location permissions
  const checkPermissions = () => {
    navigator.permissions.query({name:'geolocation'}).then(function(result) {
      // Will return ['granted', 'prompt', 'denied']
      console.log(result.state);
      if(result.state === 'granted'){
        getLocation();
      }else{
        console.log('Enable location to use current location');
        const data = {
          latitude: 37.7749,
          longitude: 122.4194
        }
        getWeather(data)
      }
    });
  }


  //State changed
  useEffect(() => {
    checkPermissions();
    setLoading(true);
    loaderAnim();
    animContainer.current = animContainer.current.slice(0, userLocation.daily.length);
  },[])

 //Get location
 const getLocation = async () => {
  await navigator.geolocation.getCurrentPosition((position)=> {
    const currentLocation = position.coords;
    console.log(`https://api.openweathermap.org/data/2.5/onecall?lat=${currentLocation.latitude}&lon=${currentLocation.longitude}&units=imperial&appid=${apiKey}`);
    getWeather(currentLocation);
    setUsingCurrentLocation(true);
  })
 }

  //Get custom location
  const getCustomLocation = async (data) => {
    console.log(data);
    await fetch(`https://api.openweathermap.org/data/2.5/onecall?lat=${data.inputLatitude}&lon=${data.inputLongitude}&units=imperial&appid=${apiKey}`)
      .then(response => response.json())
      .then((data) => {

        setUsingCurrentLocation(false);

        console.log(data);

        //User location
        setUserLocation(data);

        const weatherData = data.daily;
        setWeather(weatherData);

        //Set days
        let newDays = [];
        data.daily.map((day) => {
          let dayInt = new Date(day.dt*1000).toDateString();
          newDays.push(dayInt);
        })

        //Loading set to false
        setLoading(false);

        setDays(newDays);
      })
   }


  //Get weather
  const getWeather = async (currentLocation) => {
    await fetch(`https://api.openweathermap.org/data/2.5/onecall?lat=${currentLocation.latitude}&lon=${currentLocation.longitude}&units=imperial&appid=${apiKey}`)
      .then(response => response.json())
      .then((data) => {


        console.log(data);

        setUsingCurrentLocation(true);

        //User location
        setUserLocation(data);

        //Set coordinates
        setLatitude(currentLocation.latitude);
        setlongitude(currentLocation.longitude);

        const weatherData = data.daily;
        setWeather(weatherData);

        //Set days
        let newDays = [];
        data.daily.map((day) => {
          let dayInt = new Date(day.dt*1000).toDateString();
          newDays.push(dayInt);
        })

        //Loading set to false
        setLoading(false);

        setDays(newDays);
      })
      .then(()=>{
        sunnyAnim();
      })
    }

    //Card
    function cardHandler(e, day) {
      e.preventDefault();
      console.log(day);
      setCardDetail(day);
      setCardDetailVisible(true);
    }

    function handleCardClose(){
      console.log('Closing card');
      setCardDetailVisible(false);
    }

  return (
    <div id='page-container'>
      <CardDetail handleCardClose={() => handleCardClose} cardDetailVisible={cardDetailVisible} dailyDetails={cardDetail}/>
      <div id='weather-container'>
      <div id='weather'>
        <div id='loader' ref={container} style={{display: loading ? 'block' : 'none' }} ></div>
        <div id='weather-form'>
          {loading ? <h1>Predicting weather for...</h1> : <h1>{userLocation.timezone}</h1>}
          <div id='input'>
            <form onSubmit={handleSubmit(onSubmit)}>
              <input type="number" id='inputField' {...register('inputLatitude', {valueAsNumber: true}, {required: true})} placeholder='Latitude'></input>
              <input type="number" id='inputField' {...register('inputLongitude', {valueAsNumber: true}, {required: true})} placeholder='longitude'></input>
              <button>Search location</button>
            </form>
            {usingCurrentLocation ? <button onClick={() => getLocation()}>{userLocation.timezone}</button> : <button onClick={() => getLocation()}>Use current location</button>}
          </div>
        </div>
        {loading ? <PlaceholderCards/> :
        <div id='day-card-container'>
          <div id='arrow-left'>
          <svg width="60" height="60" viewBox="0 0 60 60" fill="none" xmlns="http://www.w3.org/2000/svg"><circle cx="30" cy="30" r="29.5" fill="#2C2C2C" stroke="white"/><path d="M26.6066 19L37.2132 29.6066L26.6066 40.2132" stroke="white"/></svg>
          </div>
          <div id='arrow-right'>
          <svg width="60" height="60" viewBox="0 0 60 60" fill="none" xmlns="http://www.w3.org/2000/svg"><circle cx="30" cy="30" r="29.5" fill="#2C2C2C" stroke="white"/><path d="M26.6066 19L37.2132 29.6066L26.6066 40.2132" stroke="white"/></svg>
          </div>
          {userLocation.daily?.map((day,index) => (
          <div id='day-card' key={index} onClick={(e) => cardHandler(e, day)}>
              <div id='card-image' ref={animContainer}></div>
              <h3 id='weather-text'>{day.weather[0].main}</h3>
              <h2 id='day-text'>{moment.unix(day.dt).format("dddd MM/DD")}</h2>
              <ul id='weather-list'>
                <li>
                  <h4 id='list-sub'>High of</h4>
                  <h4 id='list-header'>{day.temp.max}</h4>
                </li>
                <li>
                  <h4 id='list-sub'>High of</h4>
                  <h4 id='list-header'>{day.temp.max}</h4>
                </li>
                <li>
                  <h4 id='list-sub'>High of</h4>
                  <h4 id='list-header'>{day.temp.max}</h4>
                </li>
                <li>
                  <h4 id='list-sub'>High of</h4>
                  <h4 id='list-header'>{day.temp.max}</h4>
                </li>
              </ul>
            </div>
        ))}</div>}
      </div>
      </div>
    </div>
  );
}

export default App;
