import React, { useEffect, useState } from 'react';
const weatherAPI = {
  key: "8bd04b4c4bc8f493a35b227dba34ae78",
  baseURL: "http://api.weatherapi.com/v1",
  lat: 10,
  lon: 10
}

function App() {

  const [location, setLocation] = useState({
    lat: null,
    lon: null
  })

  useEffect(() => {
    //Get current position
    navigator.geolocation.getCurrentPosition((position)=> {
      const p = position.coords;

      //Set current location
      setLocation({
        lat: p.latitude,
        lon: p.longitude
      })
    })

    console.log(location);

  },[])

  async function getWeatherData(){
    await fetch(`https://api.openweathermap.org/data/2.5/forecast?lat=${location.lat}&lon=${location.lon}&appid=8bd04b4c4bc8f493a35b227dba34ae78`)
      .then(response => response.json())
      .then(data => console.log(data));
  }

  //Get user location
  //Get weather data
  //Display users data based on location

  return (
    <div>
      <h1>Weather app</h1>
    </div>
  );
}

export default App;
