import React, { useEffect, useState } from 'react';

function App() {

  //Set initial location state
  const [location, setLocation] = useState({
    lat: null,
    lon: null
  })

  //State changed
  useEffect(() => {
    getLocation();
  },[])

  //Get location
  const getLocation = () => {
    navigator.geolocation.getCurrentPosition((position)=> {
      const x = position.coords;
      setLocation({
        lat: x.latitude,
        lon: x.longitude
      })
    })
    console.log(location);
  }

  //Get weather
  const getWeather = async () => {
    await fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${location.lat}&lon=${location.lon}&appid=8bd04b4c4bc8f493a35b227dba34ae78`)
      .then(response => response.json())
      .then(data => console.log(data))
  }



  return (
    <div>
      <h1>Weather app</h1>
    </div>
  );
}

export default App;
