import React, { useEffect, useState } from 'react';
import moment from 'moment';

function App() {

  const [latitude, setLatitude] = useState(0);
  const [longitude, setlongitude] = useState(0);
  const [userLocation, setUserLocation] = useState([]);
  const [weather, setWeather] = useState([]);
  const [days, setDays] = useState([]);
  const [loading, setLoading] = useState();

    //State changed
    useEffect(() => {
      getLocation();
    },[])

 //Get location
 const getLocation = async () => {
  await navigator.geolocation.getCurrentPosition((position)=> {
    const currentLocation = position.coords;
    getWeather(currentLocation);
  })
 }

  //Get weather
  const getWeather = async (currentLocation) => {
    await fetch(`https://api.openweathermap.org/data/2.5/onecall?lat=${currentLocation.latitude}&lon=${currentLocation.longitude}&units=imperial&appid=8bd04b4c4bc8f493a35b227dba34ae78`)
      .then(response => response.json())
      .then((data) => {

        console.log(data);

        //User location
        setUserLocation(data);

        //Set coordinates
        setLatitude(currentLocation.latitude);
        setlongitude(currentLocation.longitude);

        const weatherData = data.daily;
        setWeather(weatherData);

        //Set days
        let newDays = [];
        data.daily.map((day) => {
          let dayInt = new Date(day.dt*1000).toDateString();
          newDays.push(dayInt);
        })

        setDays(newDays);
      })
  }


  return (
    <div>
      <h1>Weather app</h1>
      <h2>{userLocation.timezone}</h2>
      <div>{userLocation.daily?.map((day,index) => (
        <div key={index}>
          <h1>{moment.unix(day.dt).format("dddd MM/DD")}</h1>
          <h2>{day.weather[0].main}</h2>
          <h4>{`High of ${day.temp.max}`}</h4>
          <h4>{`Low of ${day.temp.min}`}</h4>
          </div>
      ))}</div>
    </div>
  );
}

export default App;
