import React, { useEffect, useState } from 'react';

function App() {

  const [latitude, setLatitude] = useState(0);
  const [longitude, setlongitude] = useState(0);
  const [weather, setWeather] = useState([{}]);
  const [days, setDays] = useState([]);

    //State changed
    useEffect(() => {
      getLocation();
    },[])

 //Get location
 const getLocation = async () => {
  await navigator.geolocation.getCurrentPosition((position)=> {
    const currentLocation = position.coords;
    // setLatitude(currentLocation.latitude);
    // setlongitude(currentLocation.longitude);
    getWeather(currentLocation);
  })
 }

  //Get weather
  const getWeather = async (currentLocation) => {
    await fetch(`https://api.openweathermap.org/data/2.5/onecall?lat=${currentLocation.latitude}&lon=${currentLocation.longitude}&appid=8bd04b4c4bc8f493a35b227dba34ae78`)
      .then(response => response.json())
      .then((data) => {
        console.log(data);

        //Set coordinates
        setLatitude(currentLocation.latitude);
        setlongitude(currentLocation.longitude);

        //Set daily weather
        const newWeather = data.daily;
        console.log(newWeather);

        //Set days
        let newDays = [];
        data.daily.map((day) => {
          let dayInt = new Date(day.dt*1000).toDateString();
          newDays.push(dayInt);
        })
        console.log(newDays);
        setDays(newDays);
      })
  }


  return (
    <div>
      <h1>Weather app</h1>
      <h2>{latitude}</h2>
      <h2>{longitude}</h2>

      {days.map((day, index) => (
        <h1 key={index}>{day}</h1>
      ))}
    </div>
  );
}

export default App;
