import React, { useEffect, useState } from 'react';

function App() {

  const [userLocation, setUserLocation] = useState({
    lat: 0,
    lon: 0
  });

  const [days, setDays] = useState([]);

 //Get location
 const getLocation = async () => {
  await navigator.geolocation.getCurrentPosition((position)=> {
    const x = position.coords;
    setUserLocation({
      lat: x.latitude,
      lon: x.longitude
    })
  })
 }

  //State changed
  useEffect(() => {
    getLocation();
    getWeather();
  },[])


  //Get weather
  const getWeather = async () => {
    await fetch(`https://api.openweathermap.org/data/2.5/forecast?lat=${userLocation.lat}&lon=${userLocation.lon}&appid=8bd04b4c4bc8f493a35b227dba34ae78`)
      .then(response => response.json())
      .then((data) => {
        console.log(data);
        const newDays = data.list;
        setDays(newDays);

        //Days
        data.list.map((day,index) => {
          let dtInt = new Date(day.dt*1000);
          let dtString = dtInt.toDateString();
          console.log(dtString);
          Array.from(dtString);
        })
      })
  }


  return (
    <div>
      <h1>Weather app</h1>
      <div>
        {days.map((day) => (
          <div key={day.dt}>
            <h1>{day.weather[0].description}</h1>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
