import React, { useEffect, useRef, useState } from 'react';
import moment from 'moment';
import '../src/App.scss';
import lottie from 'lottie-web';
import { useForm, useWatch } from 'react-hook-form';
import PlaceholderCards from './Components/PlaceholderCards';
import Lottie from 'lottie-web';
import CardDetail from './Components/CardDetail';


function App() {

  const apiKey = "8bd04b4c4bc8f493a35b227dba34ae78";

  const [latitude, setLatitude] = useState(0);
  const [longitude, setlongitude] = useState(0);
  const [userLocation, setUserLocation] = useState([]);
  const [weather, setWeather] = useState([]);
  const [days, setDays] = useState([]);
  const [loading, setLoading] = useState(false);
  const [customLocation, setCustomLocation] = useState({
    customLatitude: null,
    customLongitude: null
  })
  const [cardDetail, setCardDetail] = useState([]);

  const {register,watch,handleSubmit,formState: { errors },} = useForm();
  const onSubmit = (data) => getCustomLocation(data);

  const container = useRef(null);

  const loaderAnim = () => {
    lottie.loadAnimation({
      container: container.current,
      renderer: 'svg',
      loop: true,
      autoplay: true,
      animationData: require('../src/svg/loader.json')
    })
  }

  //Check for location permissions
  const checkPermissions = () => {
    navigator.permissions.query({name:'geolocation'}).then(function(result) {
      // Will return ['granted', 'prompt', 'denied']
      console.log(result.state);
      if(result.state === 'granted'){
        getLocation();
      }else{
        console.log('Enable location to use current location');
        const data = {
          latitude: 37.7749,
          longitude: 122.4194
        }
        getWeather(data)
      }
    });
  }


  //State changed
  useEffect(() => {
    checkPermissions();
    setLoading(true);
    loaderAnim();
  },[])

 //Get location
 const getLocation = async () => {
  await navigator.geolocation.getCurrentPosition((position)=> {
    const currentLocation = position.coords;
    console.log(`https://api.openweathermap.org/data/2.5/onecall?lat=${currentLocation.latitude}&lon=${currentLocation.longitude}&units=imperial&appid=${apiKey}`);

    getWeather(currentLocation);
  })
 }

  //Get custom location
  const getCustomLocation = async (data) => {
    console.log(data);
    await fetch(`https://api.openweathermap.org/data/2.5/onecall?lat=${data.inputLatitude}&lon=${data.inputLongitude}&units=imperial&appid=${apiKey}`)
      .then(response => response.json())
      .then((data) => {

        //User location
        setUserLocation(data);

        const weatherData = data.daily;
        setWeather(weatherData);

        //Set days
        let newDays = [];
        data.daily.map((day) => {
          let dayInt = new Date(day.dt*1000).toDateString();
          newDays.push(dayInt);
        })

        //Loading set to false
        setLoading(false);

        setDays(newDays);
      })
   }


  //Get weather
  const getWeather = async (currentLocation) => {
    await fetch(`https://api.openweathermap.org/data/2.5/onecall?lat=${currentLocation.latitude}&lon=${currentLocation.longitude}&units=imperial&appid=${apiKey}`)
      .then(response => response.json())
      .then((data) => {

        //User location
        setUserLocation(data);

        //Set coordinates
        setLatitude(currentLocation.latitude);
        setlongitude(currentLocation.longitude);

        const weatherData = data.daily;
        setWeather(weatherData);

        //Set days
        let newDays = [];
        data.daily.map((day) => {
          let dayInt = new Date(day.dt*1000).toDateString();
          newDays.push(dayInt);
        })

        //Loading set to false
        setLoading(false);

        setDays(newDays);
      })
    }

    function cardHandler(e, day) {
      e.preventDefault();
      console.log(day);
      setCardDetail(day);
    }

  return (
    <div>
      <CardDetail dailyDetails={cardDetail}/>
      <div id='weather-container'>
      <div id='weather'>
        <div id='loader' ref={container} style={{display: loading ? 'block' : 'none' }} ></div>
        <div id='weather-form'>
          <h1>Weather app</h1>
          <button onClick={() => getLocation()}>Use current location</button>
          <form onSubmit={handleSubmit(onSubmit)}>
            <input {...register('inputLatitude', {required: true})} placeholder='Latitude'></input>
            <input {...register('inputLongitude', {required: true})} placeholder='longitude'></input>
            <button>Search location</button>
          </form>
          {loading ? <h2 style={{ opacity: 0 }}>ASDF</h2> : <h2>{userLocation.timezone}</h2>}
        </div>
        {loading ? <PlaceholderCards/> :
        <div id='day-card-container'>{userLocation.daily?.map((day,index) => (
          <div id='day-card' key={index} onClick={(e) => cardHandler(e, day)}>
              <h2>{moment.unix(day.dt).format("dddd MM/DD")}</h2>
              <br/>
              <h2>{day.weather[0].main}</h2>
              <br/>
              <h4>{`High of ${day.temp.max}`}</h4>
               <br/>
              <h4>{`Low of ${day.temp.min}`}</h4>
            </div>
        ))}</div>}
      </div>
      </div>
    </div>
  );
}

export default App;
