import React, { useEffect, useState } from 'react';

function App() {

  const [userLocation, setUserLocation] = useState({
    lat: 0,
    lon: 0
  });

  const [days, setDays] = useState([]);

 //Get location
 const getLocation = async () => {
  await navigator.geolocation.getCurrentPosition((position)=> {
    const x = position.coords;
    console.log(x);
    const newLocation = {
      lat: x.latitude,
      lon: x.longitude
    }
    setUserLocation(newLocation);
  })
  console.log(userLocation);
 }

  //Get weather
  const getWeather = async () => {
    await fetch(`https://api.openweathermap.org/data/2.5/onecall?lat=${userLocation.lat}&lon=${userLocation.lon}&appid=8bd04b4c4bc8f493a35b227dba34ae78`)
      .then(response => response.json())
      .then((data) => {
        console.log(data);
      })
  }

  //State changed
  useEffect(() => {
    getLocation();
    getWeather();
  },[])



  return (
    <div>
      <h1>Weather app</h1>
      <div>
      </div>
    </div>
  );
}

export default App;
